INSERT INTO `jform_cgreport_head` VALUES ('402880e74ea46b1d014ea47223a60001','select\r\n\tSTORAGE_ID,\r\n\tSTORAGE_NAME,\r\n\tSTORAGE_IP,\r\n\tSTORAGE_PORT,\r\n\tDATA_PORT,\r\n\tSPACE,\r\n\tSPACE_MIN,\r\n\tSTATUS,\r\n\tSPACE_USED,\r\n\tSPACE_UN,\r\n\tEMP_ID,\r\n\tEMP_NAME,\r\n\tTEL,\r\n\tdepartname,\r\n\twarnflag\r\nfrom\r\n(\r\nselect \r\n\ta.STORAGE_ID,\r\n\ta.STORAGE_NAME,\r\n\ta.STORAGE_IP,\r\n\ta.STORAGE_PORT,\r\n\ta.DATA_PORT,\r\n\ta.SPACE,\r\n\ta.SPACE_MIN,\r\n\ta.STATUS,\r\n\ta.SPACE_USED,\r\n\ta.SPACE_UN,\r\n\ta.EMP_ID,\r\n\tb.EMP_NAME,\r\n\tb.TEL,\r\n\tc.departname,\r\n\tcase when a.space_un is null then \"02\"\r\n\telse \r\n\t\tcase when a.space_min>a.space_un then \"01\" else \"02\" end \r\n\tend warnflag\r\nfrom \r\nstorage a left join employee b on a.EMP_ID=b.EMP_ID \r\nleft join t_s_depart c on a.BANK_ID=c.ID\r\n)b\r\n','storagereport','存储设备查询','存储设备查询'),('402880e74ea46b1d014ea4900298001a','SELECT\r\n\tA.terminal_id,\r\n\tA.terminal_name,\r\n\tA.TERMINAL_IP,\r\n\tA.TERMINAL_PORT,\r\n\td.STORAGE_NAME,\r\n\tA.status,\r\n\tA.UPLOAD_TIME,\r\n\tA.PRE_TIME,\r\n\tA.DELAY_TIME,\r\n\tA.TIME_END,\r\n\tA.is_normal,\r\n\tB.departname,\r\n\tC.EMP_NAME,\r\n\tC.TEL\r\nFROM\r\n\tterminal A LEFT JOIN t_s_depart B ON A.BANK_ID=B.ID\r\n\tLEFT JOIN employee C ON A.EMP_ID=C.EMP_ID\r\n\tleft join storage d on a.STORAGE_Id=d.STORAGE_ID','terminalreport','终端报表','终端报表'),('402880e74ea62d70014ea6424f810004','select\r\nbill_id,a.channel_id,file_url,file_name,begin_time,end_time,a.status,file_rate,is_del,b.CHANNEL_NAME\r\nfrom bill_detail a left join channel b on a.CHANNEL_ID=b.CHANNEL_ID','filldetailreport','文件明细报表','文件明细报表'),('402880ea4e9c2863014e9c339c3c0001','select\r\n\ta.channel_id,a.channel_name,b.officee_name,c.terminal_name,d.departname,a.status,a.is_work\r\nfrom\r\n\tchannel a\r\n\tleft join office b on a.bank_id=b.bank_id\r\n\tleft join terminal c on b.terminal_id=c.terminal_id\r\n\tleft join t_s_depart d on a.bank_id=d.id','chanelreport','通道查询','通道查询');

